import "./App.css";
import React, { useState,useEffect } from "react";
import Axios from "axios";
import TextField from "@mui/material/TextField";
import { useNavigate,useParams } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
// import validator from "validator";


const initialState = {
    email1: "",
    mobile1: "",
    pass: "",
  };
function Update() {
  const [emailError, setEmailError] = useState("");
//   const [email1, setEmail1] = useState("");
//   const [mobile1, setMobile1] = useState("");
//   const [pass, setPass] = useState("");
//   const [error] = useState(false);
const [kush, setKush] = useState(initialState);
const { email1, mobile1, pass } = kush;
const [error1] = useState(false);

//   const navigate = useNavigate();
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    Axios.get(`http://localhost:3500/api/get/${id}`).then((resp) => {
      if (resp.data.success === false) {
        setKush(resp.data.message);
      } else {
        setKush({ ...resp.data.result[0] });
      }
      toast.success(resp.data.message);
    });
  }, [id]);
  const handleSubmit=()=>{
if(!email1||!mobile1||!pass)
{
    alert("Fill All The Details Carefully")
}else{

}
  }
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setKush({ ...kush, [name]: value });
  };
  const downkey = ["e", "E", "+", "-", "."];

  return (
    <div className="App">
      <ToastContainer style={{zIndex:"10"}}/>
      <br />
      <form
        style={{
          alignItems: "center",
          display: "flex",
          margin: "auto",
          textAlign: "center",
          justifyContent: "center",
          marginTop:'12%'
        }}
      >
        <div
          style={{
            backgroundColor: "lightgray",
            borderRadius: "20px",
            width: "50%",
          }}
        >
      <h1>Register Here</h1>
          <TextField
            type="email"
            label="Email"
            variant="outlined"
            className="kush"
            placeholder="EmailId"
            value={email1}
            onChange={handleInputChange}
            style={{
            //   marginTop: "30px",
            }}
          />
          {/* <br></br> */}
          {/* <button type="button" style={{
              marginTop: "30px",
            }} disabled={mobile1.length>=11}><b>Verify</b></button> */}
          <br />
          <span style={{color:"red"}}>{emailError}</span>
          <br />
          <br />

          <TextField
            type="number"
            label="Mobile"
            variant="outlined"
            className="kush"
            placeholder="Mobile No"
            value={mobile1}
            onChange={handleInputChange}
            onKeyDown={(e) => downkey.includes(e.key) && e.preventDefault()}
          /> 
          {error || mobile1.length > 10 ? (
            <span
              style={{
                textAlign: "center",
                margin: "auto",
                display: "block",
                color: "red",
              }}
            >
              Mobile cannot be more than 10
            </span>
          ) : (
            ""
          )}
          <br/>
          <br/>
          <br/>
          <TextField
            type="password"
            label="Password"
            variant="outlined"
            className="kush"
            placeholder="Password"
            value={pass}
            onChange={handleInputChange}
          />
          <br />
          {error || pass.length > 10 ? (
            <span
              style={{
                textAlign: "center",
                margin: "auto",
                display: "block",
                color: "red",
              }}
            >
              Password cannot be more than 10
            </span>
          ) : (
            ""
          )}
          <br />
          <br />
          <button
            type="button"
            style={{ padding: "20px", marginBottom: "25px" }}
            onClick={handleSubmit}
            disabled={
                mobile1.length>10 ||
                mobile1.length<10 ||
                pass.length<4||
                pass.length>10||
                email1.length<8
            }
          >
            Sign Up
          </button>
          &nbsp;
          {/* <button
            type="button"
            style={{ padding: "20px", marginBottom: "25px" }} */}
            {/* // onClick={handleCheck} */}
          {/* > */}
            {/* Check
          </button> */}
        </div>
      </form>
    </div>
  );
}

export default Update;
