import Axios  from 'axios';
import React,{useState,useEffect} from 'react'
import "./App.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
function Dashboard() {
    const [data, setData] = useState([]);

  const loadData = async () => {
    const response = await Axios("http://localhost:7000/get");
    setData(response.data);
  };

  useEffect(() => {
    loadData();
  }, []);
  const handleDelete = (id) => {
    if (window.confirm("Are You Sure You Want To Delete")) {
      Axios.delete(`http://localhost:3500/delete/${id}`);
      toast.success("Deleted Successfully", { position: "top-center" });
      setTimeout(() => loadData(), 500);
    }
  };
  return (
    <div>
    <ToastContainer/>
      <table>
                  <thead>
                    <tr>
                      {/* <th>Id</th> */}
                      <th>Email Id</th>
                      <th>Mobile No.</th>
                      <th>Password</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {data.map((item)=>{
                        return(
                            <>
                                <tr>
                                    <td>{item.email}</td>
                                    <td>{item.mobile}</td>
                                    <td>{item.password}</td>
                                    <td>
                                        <button style={{ padding: "8px"}}>Edit</button> &nbsp;
                                        <button style={{ padding: "8px"}} onClick={() => handleDelete(item.id)}>Delete</button>
                                    </td>
                                </tr>
                            </>
                        )
                    })}
                  </tbody>
                </table>
    </div>
  )
}

export default Dashboard
