import "./App.css";
import React, { useState } from "react";
import Axios from "axios";
import TextField from "@mui/material/TextField";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function Register() {
  const [email1, setEmail1] = useState("");
  const [mobile1, setMobile1] = useState("");
  const [pass, setPass] = useState("");
  // console.log(email1,mobile1,pass)
  const handleEmailChange = (e) => {
    setEmail1(e.target.value);
  };
  const handleMobileChange = (e) => {
    setMobile1(e.target.value);
  };
  const handlePassChange = (e) => {
    setPass(e.target.value);
  };
  const navigate = useNavigate();
  const handleSubmit = () => {
    if (!email1 || !mobile1 || !pass) {
      alert("please fill the form properly");
    } else {
      Axios.post("http://localhost:7000/register", {
        email: email1,
        mobile: mobile1,
        password: pass,
      }).then((response) => {
        console.log(response);
      });
      setEmail1("");
      setMobile1("");
      setPass("");
      navigate("/login");
    }
  };
  const handleCheck = () => {
    Axios.post("http://localhost:7000/verify", {
      email: email1,
      password: pass,
    }).then((response) => {
      if (response.data.success === true) {
        toast.error(response.data.message);
      } else {
        toast.success("Registered Successfully");
      }
    });
  };
  return (
    <div className="App">
      <br />
      <ToastContainer />
      <form
        style={{
          alignItems: "center",
          display: "flex",
          margin: "auto",
          textAlign: "center",
          justifyContent: "center",
        }}
      >
        <div
          style={{
            backgroundColor: "lightgray",
            borderRadius: "20px",
            width: "50%",
          }}
        >
          <TextField
            type="email"
            label="Email"
            variant="outlined"
            className="kush"
            placeholder="EmailId"
            value={email1}
            onChange={(e) => handleEmailChange(e)}
            style={{
          marginTop:"30px"

            }}
          />
          <br />
          <br />
          <TextField
            type="number"
            label="Mobile"
            variant="outlined"
            className="kush"
            placeholder="Mobile No"
            value={mobile1}
            onChange={(e) => handleMobileChange(e)}
            onKeyDown={(e) => downkey.includes(e.key) && e.preventDefault()}
          />
           <br />
          {error || mobile1.length > 10 ? (
            <small style={{ textAlign: "center", margin: "auto", display: "block", color: "red" }}>
              Mobile cannot be more than 10
            </small>
          ) : (
            ""
          )}
          <br />
          <br />
          <TextField
            type="password"
            label="Password"
            variant="outlined"
            className="kush"
            placeholder="Password"
            value={pass}
            onChange={(e) => handlePassChange(e)}
            onKeyDown={(e) => downkey.includes(e.key) && e.preventDefault()}

          />
          <br />
          <br />
          <button
            type="button"
            style={{ padding: "20px",marginBottom:"25px" }}
            onClick={handleSubmit}
          >
            Submit
          </button>
          &nbsp;
          <button
            type="button"
            style={{ padding: "20px",marginBottom:"25px" }}
            onClick={handleCheck}
          >
            Check
          </button>
        </div>
      </form>
    </div>
  );
}

export default Register;
